package groovy.lab

import groovy.json.JsonOutput
import groovy.json.JsonSlurper
import org.apache.commons.lang3.CharEncoding
import org.apache.commons.io.IOUtils;

/**
 * Created by I340951 on 8/14/2017.
 */
class MyJsonLab {
    final static String demoJsonString = IOUtils.toString(this.getClass().getResourceAsStream("/json/metadata.json"),
            CharEncoding.UTF_8
    )

    static void demo1() {
        println demoJsonString
    }

    //URL: https://blog.csdn.net/u011054333/article/details/60498510
    //parseSimpleObject
    static void demo2() {
        def book = new Book(id: 1, name: 'Thinking in Java')
        def jsonOutput = new JsonOutput()
        def result = jsonOutput.toJson(book)
        println(result)

        //格式化输出
        println(jsonOutput.prettyPrint(result))
    }

    //URL: https://blog.csdn.net/u011054333/article/details/60498510
    //parseSimpleJson
    static void demo3() {
        def text = '{"id":1,"name":"Thinking in Java"}'
        def jsonSlurper = new JsonSlurper()

        //获取到的是Map对象
        def map = jsonSlurper.parseText(text)
        println(map)
        //将Map转换为实际对象
        def obj = new Book(map)
        println(obj)
    }

    //URL: https://cloud.tencent.com/developer/ask/179186
    static void demo4() {
        def str = '[[{"ABC":{"total":0,"failed":0,"skipped":0}}], [{"BCD": {"total":0,"failed":0,"skipped":0}}]]'
        def parser = new JsonSlurper()
        def json = parser.parseText(str)
        assert json[0][0].ABC.total == 0
        assert json[0][0].ABC.failed == 0
        assert json[0][0].ABC.skipped == 0
        assert json[1][0].BCD.total == 0
        assert json[1][0].BCD.failed == 0
        println json
    }

